<template>
  <li class="songsList" @click="toplaylist">
    <img :src="playlist.picUrl" />
    <span class="play-count">{{ playlist.playCount | playCount }}</span>
    <p>{{ playlist.name }}</p>
  </li>
</template>

<script>
export default {
  name: "SongsList",
  props: ["playlist"],
  //   过滤器
  filters: {
    playCount: function (value) {
      if (value / 100000000 >= 1) {
        return (value / 100000000).toFixed(1) + "亿";
      } else if (value / 10000 >= 10) {
        return (value / 10000).toFixed(1) + "万";
      } else {
        return value;
      }
    },
  },
  methods: {
      toplaylist: function () {
          this.$router.push({path: '/playlist', query: {id: this.playlist.id}});
          // this.$router.push('/playlist/id='+this.playlist.id);
      }
  }
};
</script>

<style lang="less">
li.songsList {
  position: relative;
  flex: 1 1 30vw;
  margin-bottom: 15px;
  img {
    vertical-align: middle;
    height: 33vw;
  }
  .play-count {
    &::before {
        content: '';
        display: inline-block;
        vertical-align: middle;
        width: 1.2em;
        height: 0.9em;
        background: url(data:image/svg+xml;base64,) no-repeat;
    }
    position: absolute;
    top: 3px;
    right: 4px;
    color: #fff;
    text-shadow: 0px 1px 0px #000;
    font-size: 12px;
  }
  p {
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
    overflow: hidden;
    font-size: 13px;
    padding: 6px 2px 0 6px;
  }
  &:nth-of-type(2),
  &:nth-of-type(5) {
    margin: 0 5px;
  }
}
</style>